text = """"""


replace = [["AD", "d"], ["DF", "e"], ["AA", "a"], ["GD", "r"], ["GV", "u"], ["FG", "n"], ["DD", "c"], ["FD", "l"], ["GX", "w"], ["DV", "i"], ["DG", "h"], ["FF", "m"], ["FV", "o"], ["AV", "v"], ["GG", "t"], ["GF", "s"], ["AG", "g"], ["DA", "b"], ["VA", "y"], ["AF", "f"], ["FX", "p"], ["FA", "k"], ["DX", "j"], ["GA", "q"]]

K = 2
chars = [text[i: j] for i in range(0, len(text), 2) for j in range(i + 1, len(text) + 1) if len(text[i:j]) == K]

    
for char_replace in replace:
    try:
        while True:
            chars[chars.index(char_replace[0])] =  char_replace[1]
    except:
        continue
# [x for ind in text if ind.isupper()]
for char in chars:
    print(char, end='')

